from PIL import Image

chall = Image.open('chall.png')
chall_x,chall_y = chall.size[0],chall.size[1]
raw = Image.open('raw.png')
raw_x,raw_y = raw.size[0],raw.size[1]

binary_string = ""

for i in range(chall_x):
    for j in range(chall_y):
        chall_pixel = chall.getpixel((i,j))
        raw_pixel = raw.getpixel((i,j))
        if chall_pixel[0] != raw_pixel[0]:
            #print(chall_pixel , raw_pixel)
            result = int(chall_pixel[0]) - int(raw_pixel[0])
            if result == -1:
                result = 0
            binary_string += str(result)

print(binary_string) #58 x 58

#binary_string = ""

new_image = Image.new('RGB',(58,58))

for i in range(58):
    for j in range(58):
        if binary_string[i*58 + j] == "0":
            new_image.putpixel((i,j),(200,0,0))

new_image.show()

